__author__ = 'Pawel'from train import Trainclass TrainBuilder(object):    def __init__(self):        """        :return:        """        self.speed = 0        self.start = 0        self.finish = 0        self.if_moving = None        self.can_move = None        self.unblock = None        self.direction = 0        self.value = None        self.pos_x = 0        self.pos_y = 0        self.animation = None        self.time = None    def set_start_station(self, station):        """        :param station:        :return:        """        self.start = station        self.direction = station.rotation        self.pos_x = station.x        self.pos_y = station.y    def set_finish_station(self, station):        """        :param station:        :return:        """        if station < 0:            self.finish = 0        else:            self.finish = station    def set_speed(self, speed):        """        :param speed:        :return:        """        if speed > 0:            self.speed = speed        else:            self.speed = 0    def set_value(self, value):        """        :param value:        :return:        """        if value > 0:            self.value = value        else:            self.value = 0    def set_bool_values(self, if_moving, can_move, unblock):        """        :param if_moving:        :param can_move:        :param unblock:        :return:        """        self.if_moving = if_moving        self.can_move = can_move        self.unblock = unblock    def set_animation(self, animation):        """        :param animation:        :return:        """        self.animation = animation    def set_time(self, time):        """        :param time:        :return:        """        self.time = time    def create_train(self):        """        :return:        """        return Train(self.speed, self.start, self.finish, self.if_moving, self.can_move,                     self.unblock, self.direction, self.value, self.pos_x, self.pos_y,                     self.animation, self.time)